import React, { useEffect, useState } from "react";
import ResolCSS from '../styles/Resolution.module.css'

import Resolution from "./Resolution";

export default function ResolutionForm() {
    let [data, setData] = useState([])

    const getTest = async () => {
        let paramId = window.location.pathname.split('/')[2]
        let dataProcess = {}
        let dataFromServer = await fetch(`/resolution/${paramId}`).then(res => res.json()).then(r => r.data) /* 
            {0_answerFalse
: 
(3) ['1234', '1111', '22222']
0_answerTrue
: 
"qwer"
0_textQUE
: 
"text"
0_type
: 
"checkbox"
1_answerTrue
: 
(4) ['2222', '222', '22', '2']
1_textQUE
: 
"sdasdasdasd"
1_type
: 
"text"
2_answerFalse
: 
(3) ['qqq', 'qwe', 'ewq']
2_answerTrue
: 
"wwwwwwwwww"
2_textQUE
: 
"31231231"
2_type
: 
"radio"
id
: 
"ASjsxA"
nameTest
: 
"1"}
        */

        console.log(dataFromServer)

        for (let key in dataFromServer) {
            if ((+key[0] || +key[0] === 0) && !key.split('_')[1].startsWith('answer')) {
                dataProcess[+key[0]] = {
                    ...dataProcess[+key[0]],
                    [key.slice(2)]: dataFromServer[key]
                }

            } else if (key.split('_')[0] === 'nameTest') {
                dataProcess.info = dataFromServer[key]

            } else if (key.split('_')[1]) {
                dataProcess[+key[0]].answer = {
                    ...dataProcess[+key[0]].answer,
                    [key]: dataFromServer[key]
                }
            }
        }

        for (let key in dataProcess) {
            if (dataProcess[key].answer) {
                dataProcess[key].answer = Object.values(dataProcess[key].answer)
            }
        }

        setData(Object.values(dataProcess))
    } // переделать эту операцию на сервер

    useEffect(() => {
        getTest()
    }, [])

    return <form method="post" action=''>
        {data ? <>

            {data.map((elem, index) => {
                if (typeof elem !== 'object') {
                    return <h3 className={ResolCSS.resH3} key={index}>{elem}</h3>
                }
            })}

            {data.map((elem, index) => {
                if (typeof elem === 'object') {
                    return <Resolution key={index} data={elem} num={index} />
                }
            })}



            <button className={ResolCSS.btnAdd} type="submit">Завершить</button>
        </>
            : <div>Загрузка</div>}
    </form>
}
