import React, { useRef, useState } from "react";

import FormCSS from './styles/Form.module.css'

export default function Form() {
    const [stateType, setStateType] = useState('radio')

    const [listAnswer, setListAnswer] = useState([])
    const [stateId, setStateId] = useState(2)
    /* 
        {
        id,
        +name,
    }*/

    function setAnswers() { // ответы
        if (stateType === 'radio') { // radio btn
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name="answerTrue_1" className={FormCSS.answer} /><br />

                    <p>Неверный: </p>
                    <input type="text" name="answerFalse_2" className={FormCSS.answer} /><br />

                    {listAnswer.map(elem => {
                        return <>
                            <input key={elem.id} type='text' name={elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id)} className={FormCSS.answer} />
                            <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id)}>удалить</button>
                            <br />
                        </>
                    })}
                </div>

                <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns('answerFalse')}>Добавить неверный</button>
            </>)


        } else if (stateType === 'text') { // text btn
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name="answerTrue_1" className={FormCSS.answer} /><br />

                    <p>Вариант написания правильного ответа: </p>
                    <input type="text" name="answerTrue_2" className={FormCSS.answer} /><br />

                    {listAnswer.map(elem => {
                        return <>
                            <input key={elem.id} type='text' name={elem.name} value={elem.text} className={FormCSS.answer} />
                            <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id)}>удалить</button>
                            <br />
                        </>
                    })}
                </div>

                <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns('answerTrue')}>Добавить вариант правильного ответа</button>
            </>)
        }
    }

    function btnAddAns(name) { // кнопка добавления ответа
        let obj = {
            id: stateId + 1,
            name: name + '_' + (stateId + 1),
            text: ''
        }
        setListAnswer([...listAnswer, obj])
        setStateId(stateId + 1)
    }// /кнопка добавления ответа

    function setTextAnsw(e, id) { // реактивная замена ответа в стейте
        let copy = [...listAnswer]
        let index = copy.findIndex(elem => elem.id === id)

        console.log(copy[index])

        // // eslint-disable-next-line no-undef
        // copy[index][text] = e.target.value
        setListAnswer(copy)
    } // /реактивная замена ответа в стейте

    function btnRemoveAns(id) { // кнопка удаления ответа
        let copy = [...listAnswer]
        let index = copy.findIndex(elem => elem.id === id)

        console.log(copy, index)

        copy.splice(index, 1)
        console.log(copy, 'final')
        setListAnswer(copy)
    }// /кнопка удаления ответа

    function setStateTypeReset(text) { // радио кнопка для выбора типа ответа 
        setStateType(text)
        setListAnswer([])
        setStateId(2)
    }// /радио кнопка для выбора типа ответа

    return (<>
        <form action="" method="post">
            <textarea className={FormCSS.textarea} name="textQUE" placeholder="Введите текст вопроса"></textarea>

            <div className={FormCSS.type}>
                <p className={FormCSS.type_para}>Выберите тип ответа:</p>
                <label><input type="radio" name="type" value='radio' onClick={() => setStateTypeReset('radio')} defaultChecked />Один вариант</label>
                <label><input type="radio" name="type" value='text' onClick={() => setStateTypeReset('text')} />Текст</label>
                <label><input type="radio" name="type" value='checkbox' onClick={() => setStateTypeReset('checkbox')} />Выбрать несколько</label>
            </div>
            {setAnswers()}
        </form>
    </>)
}
