import React, { useState } from "react";
import { nanoid } from 'nanoid';

import FormCSS from '../styles/Form.module.css'
import QUEForm from "./QUEForm";

export default function Form() {
    const [list, setList] = useState([0])
    const [count, setCount] = useState(1)

    function add() {
        let copy = [...list]
        setCount(count + 1)
        copy.push(count)
        setList(copy)
    }
    function remove(id) {
        let copy = [...list]
        let index = copy.findIndex(elem => elem === id)

        copy.splice(index, 1)
        setList(copy)
    }

    return (<>
        <form method="post">
            <input className={FormCSS.none} readOnly type="text" name="id" value={nanoid(6)} /> {/* display: none. Ставит айдишник данным для БД */}


            <input type="text" name="nameTest" className={FormCSS.inp_name} placeholder="Введите название теста" />

            {list.map((elem, index) => {
                return <QUEForm
                    key={index}
                    id={elem}
                    number={index}
                    remove={remove}
                    last={index === list.length - 1 ? true : false}
                    add={add}
                />
            })}
        </form>
    </>)
}
