import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { nanoid } from 'nanoid';

import FormCSS from '../styles/Form.module.css'
import QUEForm from "./QUEForm";

export default function Form() {
    let params = useParams()

    const [list, setList] = useState([
        {
            id: nanoid(5),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
    ])
    const [nameTest, setNameTest] = useState('')

    function add() {
        let copy = [...list]

        copy = [...copy,
        {
            id: nanoid(5),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
        ]
        setList(copy)
    }
    function remove(id) {
        let copy = [...list]
        let index = copy.findIndex(elem => elem.id === id)

        copy.splice(index, 1)
        setList(copy)
    }



    async function getDataFromDB() {
        if (params.idTest) {
            await fetch(`/create/${params.name}/${params.idTest}`)
                .then(r => r.json())
                .then(r => {
                    console.log(r.data)
                    const formattedData = r.data.map(item => ({
                        id: item.id || nanoid(5), // Используйте nanoid только если id отсутствует
                        type: item.type || 'radio', // Убедитесь, что type установлен
                        text: item.text || '',
                        answerTrue: item.answerTrue || [[nanoid(3), '']],
                        answerFalse: item.answerFalse || [[nanoid(3), '']],
                    }));
                    setList(formattedData);

                    setNameTest(r.nameTest)
                })
                .catch(err => console.log(err))
        }
    }

    useEffect(() => {
        getDataFromDB()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])
    return (<>
        <form method="post" onKeyDown={(e) => { if (e.key === 'Enter') e.preventDefault() }}>
            <input className={FormCSS.none} readOnly type="text" name="id" value={nanoid(10)} /> {/* display: none. Ставит айдишник данным для БД */}


            <input type="text" name="nameTest" className={FormCSS.inp_name} placeholder="Введите название теста" autoComplete="off" defaultValue={nameTest} />

            {list.map((elem, index) => {
                return <QUEForm
                    key={index}
                    elemFromState={elem}
                    number={index}
                    remove={remove}
                    last={index === list.length - 1}
                    add={add}

                    mainList={list}
                    mainSetList={setList}
                />
            })}
        </form>
    </>)
}
