import React, { useEffect, useState } from "react";

import ResolCSS from '../styles/Resolution.module.css'

export default function ResolutionCheck() {
    let [data, setData] = useState()

    async function getData() {
        let paramId = window.location.pathname.split('/')[2]

        let data = await fetch(`/resolution/${paramId}/check`).then(r => r.json()).then(r => r)
        setData(data)
    }

    useEffect(() => {
        getData()
    }, [])

    console.log(data)

    function render() {
        let userAnswer = data.userAnswer
        let checkAnswer = data.checkAnswer

        let count = 0
        for (let key in userAnswer) {

            return <>

                <div className={ResolCSS.res}>
                    <p className={ResolCSS.resP}>{key}</p>
                    <p>Ваш ответ: ваываываываы<span className={ResolCSS.comments}></span></p>
                </div>
            </>
        }
    }

    return <>
        {data ? <>
            <h3 className={ResolCSS.resH3}>{data.name}</h3>
            <div className={ResolCSS.res}>

                <p className={ResolCSS.resP}>Вопрос</p>
                <p>Ваш ответ: ваываываываы<span className={ResolCSS.comments}> // верно</span></p>

            </div>

            <div className={ResolCSS.res}>

                <p className={ResolCSS.resP}>Вопрос</p>
                <p>Ваш ответ: ваываываываы <span className={ResolCSS.comments}>// неверно</span></p>
                <p className={ResolCSS.comments}>// верно: "этот ответ"</p>

            </div>
        </>
            :
            <div>load</div>
        }
    </>
}
