import React, { useState } from "react";

import FormCSS from './styles/Form.module.css'

export default function Form() {
    let [state, setState] = useState()



    return (<>
        <form action="">
            <textarea className={FormCSS.textarea} name="textQUE" placeholder="Введите текст вопроса"></textarea>

            <div className={FormCSS.type}>
                <p className={FormCSS.type_para}>Выберите тип ответа:</p>
                <label><input type="radio" name="type" value='radio' />Один вариант</label>
                <label><input type="radio" name="type" value='text' />Текст</label>
                <label><input type="radio" name="type" value='checkbox' />Выбрать несколько</label>
            </div>
            {state}
        </form>
    </>)
}
