import React, { useState } from "react";

import FormCSS from './styles/Form.module.css'

export default function Form() {
    let [state, setState] = useState()

    function setTypeRadio() {
        return setState(<>
            <input type="radio" name="answer" />
        </>)
    }
    function setTypeInput() {
        return setState(<>
            <input type="text" name="answer" />
        </>)
    }
    function setTypeBox() {
        return setState(<>
            <input type="checkbox" name="answer" />
        </>)
    }

    return (<>
        <form action="">
            <textarea className={FormCSS.textarea} name="textQUE" placeholder="Введите текст вопроса"></textarea>

            <div className={FormCSS.type}>
                <p className={FormCSS.type_para}>Выберите тип ответа:</p>
                <label><input type="radio" name="type" onClick={setTypeRadio} />Один вариант</label>
                <label><input type="radio" name="type" onClick={setTypeInput} />Текст</label>
                <label><input type="radio" name="type" onClick={setTypeBox} />Выбрать несколько</label>
            </div>
        </form>
    </>)
}
