import React, { useEffect, useState } from "react";
import ResolCSS from '../styles/Resolution.module.css'

import Resolution from "./Resolution";

export default function ResolutionForm() {
    let [data, setData] = useState([])

    const getTest = async () => {
        let paramId = window.location.pathname.split('/')[2]
        let dataProcess = []
        let dataFromServer = await fetch(`/resolution/${paramId}`).then(res => res.json()).then(r => r.data)

        console.log(dataFromServer)

        let tempKey = 0
        let tempObj = {}
        // console.log(tempKey)
        for (let key in dataFromServer) {
            // console.log(key)
            if (+key[0] || +key[0] === 0) {
                tempObj[+key[0]] = {
                    ...tempObj[+key[0]],
                    [key.slice(2)]: dataFromServer[key]
                }



            } else if (key.split('_')[0] === 'nameTest') {
                dataProcess.push(dataFromServer[key])
            }
        }

        for (let key in tempObj) {
            dataProcess.push(tempObj[key])
        }

        console.log(tempObj)
        setData(dataProcess)
    } // переделать эту операцию на сервер

    useEffect(() => {
        getTest()
    }, [])
    // console.log(data)

    /*
    0: {
    textQUE: 'Когда началась ВОВ', 
    type: 'radio', 
    answerTrue_0: '1939', 
    answerFalse_0: '1941'
    }
    info: {
    id: 'ewq', 
    nameTest: 'Время начала Второй мировой войны'
    } 
    */

    /* function renderItem(data = {}) {
        for (let key in data){

        }
    } */

    return <form method="post">
        {data ? <>
            {/* <h3>{}</h3> */}



            <button className={ResolCSS.btnAdd} type="submit">Завершить</button>
        </>
            : <div>Загрузка</div>}
    </form>
}
