import React, { useState } from "react";

import FormCSS from './styles/Form.module.css'

export default function Form() {
    let [state, setState] = useState('radio')

    function setAnswers() {
        if (state === 'radio') {
            return (<>
                <label>Верный ответ: <input type="text" name="answerTrue" className={FormCSS.answer} /></label><br />
                <label>Неверный: <input type="text" name="answerFalse" className={FormCSS.answer} /></label><br />

                <button type="button">Добавить неверный</button>
            </>)
        }
    }

    function btnAddAns(type) {

    }

    return (<>
        <form action="" method="post">
            <textarea className={FormCSS.textarea} name="textQUE" placeholder="Введите текст вопроса"></textarea>

            <div className={FormCSS.type}>
                <p className={FormCSS.type_para}>Выберите тип ответа:</p>
                <label><input type="radio" name="type" value='radio' onClick={() => setState('radio')} defaultChecked />Один вариант</label>
                <label><input type="radio" name="type" value='text' onClick={() => setState('text')} />Текст</label>
                <label><input type="radio" name="type" value='checkbox' onClick={() => setState('checkbox')} />Выбрать несколько</label>
            </div>
            {setAnswers()}
            {/* правильный ответ */}
            {/* вариант */}
            {/* добавить вариант */}
        </form>
    </>)
}
