import React, { useEffect, useRef, useState } from "react";
import { useParams } from 'react-router-dom'

import PageCardTest from "./PageCardTest";
import PersCSS from '../styles/Personal.module.css'

export default function MainPagePers() {
    let [data, setData] = useState([]) // [{nameTest: 'Test', id: 'yl4hZ7', length: 1}]
    let [switchParam, setSwitchParam] = useState(0)

    let refPub = useRef()
    let refPriv = useRef()

    let params = useParams()

    async function getDataFromDB() {
        await fetch(`/personal/${params.name}`)
            .then(r => r.json())
            .then(r => {
                setData(r.data)
                console.log(r)
            })
            .catch(err => console.log(err))
    }


    useEffect(() => {
        getDataFromDB()
    }, [])
    return <>
        <div className={PersCSS.head}>
            <button ref={refPub} className={`${PersCSS.switchBtn} ${PersCSS.active}`} onClick={(e) => {
                styleBtnPrivPub()
                setSwitchParam(0)
            }}>Публичные</button>
            <button ref={refPriv} className={PersCSS.switchBtn} onClick={(e) => {
                styleBtnPrivPub()
                setSwitchParam(1)
            }}>Приватные</button>
        </div>

        <div>
            {data ?
                data.map((elem, index) => {
                    return <PageCardTest
                        key={index}
                        name={elem.nameTest}
                        length={elem.length}
                        idTest={elem.id} />
                })
                :
                'load'
            }
        </div>
    </>

    function styleBtnPrivPub() {
        let pub = refPub.current
        let priv = refPriv.current

        if (switchParam === 0) {
            pub.classList.add(PersCSS.active)
            priv.classList.remove(PersCSS.active)
        } else {
            priv.classList.add(PersCSS.active)
            pub.classList.remove(PersCSS.active)
        }
    }

