import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { nanoid } from 'nanoid';

import FormCSS from '../styles/Form.module.css'
import QUEForm from "./QUEForm";

export default function Form() {
    let params = useParams()

    const [list, setList] = useState([
        {
            id: nanoid(5),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
    ])

    function add() {
        let copy = [...list]

        copy = [...copy,
        {
            id: nanoid(5),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
        ]
        setList(copy)
    }
    function remove(id) {
        let copy = [...list]
        let index = copy.findIndex(elem => elem.id === id)

        copy.splice(index, 1)
        setList(copy)
    }



    async function getDataFromDB() {
        if (params.idTest) {
            await fetch(`/create/${params.name}/${params.idTest}`)
                .then(r => r.json())
                .then(r => setList(r.data))
                .catch(err => console.log(err))
        }

    }

    useEffect(() => {
        getDataFromDB()

        /* alert('Убедитесь, чтобы все поля были заполнены. Иначе тестируемый будет выбирать верный ответ между пустым полем и пустым полем)') */
    }, [])
    return (<>
        <form method="post" onKeyDown={(e) => { if (e.key === 'Enter') e.preventDefault() }}>
            <input className={FormCSS.none} readOnly type="text" name="id" value={nanoid(6)} /> {/* display: none. Ставит айдишник данным для БД */}


            <input type="text" name="nameTest" className={FormCSS.inp_name} placeholder="Введите название теста" autoComplete="off" />

            {list.map((elem, index) => {
                return <QUEForm
                    key={index}
                    elemFromState={elem}
                    number={index}
                    remove={remove}
                    last={index === list.length - 1}
                    add={add}

                    mainList={list}
                    mainSetList={setList}
                />
            })}
        </form>
    </>)
}
