import React, { useState } from "react";

import FormCSS from '../styles/Form.module.css'
import QUEForm from "./QUEForm";

export default function Form() {
    const [list, setList] = useState([0])
    const [count, setCount] = useState(1)
    /* {
        id,

    }
        <QUEForm number last remove add/>

        list.map((elem, index) => {
            return <QUEForm number={index} last={index === list.lenght - 1 ? true : false} remove/>
        })
    */

    function add() {
        let copy = [...list]
        copy.push(list.length)
        setList(copy)
    }
    function remove(index) {
        let copy = [...list]
        copy.splice(index, 1)
        setList(copy)
    }

    return (<>
        <form action="/" method="post">
            <input type="text" name="nameQUE" className={FormCSS.inp_name} placeholder="Введите название теста" />

            {list.map((elem, index) => {
                return <QUEForm
                    key={index}
                    number={index}
                    remove={remove}
                    last={index === list.length - 1 ? true : false}
                    add={add}
                />
            })}
        </form>
    </>)
}
