import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { nanoid } from 'nanoid';

import FormCSS from '../styles/Form.module.css'
import QUEForm from "./QUEForm";

export default function Form() {
    let params = useParams()

    const [list, setList] = useState()
    const [nameTest, setNameTest] = useState('')

    function add() {
        let copy = [...list]

        copy = [...copy,
        {
            id: nanoid(10),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
        ]
        setList(copy)
        localStorage.setItem('dataForCreate', JSON.stringify(copy))
    }
    function remove(id) {
        let copy = [...list]
        let index = copy.findIndex(elem => elem.id === id)

        copy.splice(index, 1)
        setList(copy)
        localStorage.setItem('dataForCreate', JSON.stringify(copy))
    }



    async function getDataFromDB() {
        if (params.idTest) {
            await fetch(`/create/${params.name}/${params.idTest}`)
                .then(r => r.json())
                .then(r => {
                    console.log(r.data)
                    setList(r.data)
                    setNameTest(r.nameTest)
                })
                .catch(err => console.log(err))
            return false
        }
        return true
    }

    useEffect(() => {
        if (getDataFromDB()) {
            setList(JSON.stringify(localStorage.getItem('dataForCreate')))
            setNameTest(JSON.stringify(localStorage.getItem('dataForCreateNameTest')))
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])
    return (<>
        <form method="post" onKeyDown={(e) => { if (e.key === 'Enter') e.preventDefault() }}>
            {/* <input className={FormCSS.none} readOnly type="text" name="id" value={nanoid(10)} /> {/* display: none. Ставит айдишник данным для БД */}

            {list && nameTest ? <>
                <input type="text" name="nameTest" className={FormCSS.inp_name} placeholder="Введите название теста" autoComplete="off" value={nameTest} onChange={(e) => {
                    setNameTest(e.target.value)
                    localStorage.setItem('dataForCreateNameTest', JSON.stringify(e.target.value))
                }} />

                {list.map((elem, index) => {
                    return <QUEForm
                        key={index}
                        elemFromState={elem}
                        number={index}
                        remove={remove}
                        last={index === list.length - 1}
                        add={add}

                        mainList={list}
                        mainSetList={setList}
                    />
                })}
            </>
                :
                "ывлмыдлвтм"
            }

        </form>
    </>)
}
