import React from "react";
import ResolCSS from '../styles/Resolution.module.css'

export default function Resolution({ data, num }) {

    function render() {
        if (data.type === 'radio') {
            return <>
                {data.answer.map((elem, index) => {

                    return <label className={ResolCSS.label} key={index}><input type="radio" name={num + '_answer'} />{elem}</label>

                })}
            </>
        } else if (data.type === 'checkbox') {
            return <>
                {data.answer.map((elem, index) => {

                    return <label className={ResolCSS.label} key={index}><input type="checkbox" name={num + '_answer_' + index} />{elem}</label>

                })}
            </>
        } else if (data.type === 'text') {
            return <>
                <input className={ResolCSS.input} type="text" name={num + '_answer'} />
            </>
        }
    }

    // let x = render()

    return <div className={ResolCSS.res}>
        <p className={ResolCSS.resP}>{num}. {data.textQUE}</p>

        <div className={ResolCSS.variant}>
            {render()}
        </div>

    </div>
}
