import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom'

import PageCardTest from "./PageCardTest";
import PersCSS from '../styles/Personal.module.css'

export default function MainPagePers() {
    let [data, setData] = useState([])
    let params = useParams()

    async function getDataFromDB() {
        await fetch(`/personal/${params.name}`)
            .then(r => r.json())
            .then(r => setData(r.data))
            .catch(err => console.log(err))
    }


    useEffect(() => {
        getDataFromDB()
    }, [])
    return <>
        <div className={PersCSS.head}>
            <button className={`${PersCSS.switchBtn} ${PersCSS.active}`}>Публичные</button>
            <button className={PersCSS.switchBtn}>Приватные</button>
        </div>

        <div>
            {data ?
                data.map((elem, index) => {
                    return <PageCardTest key={index} name={elem.nameTest} length={elem.length} />
                })
                :
                'load'
            }
            <PageCardTest key={6} name={'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Consequatur enim repellat sed quibusdam maxime deserunt eos, nihil ipsum repudiandae iure natus error perferendis iusto, dicta placeat expedita? Eligendi, amet molestias!'} length={15} />
        </div>
    </>
}
