import React, { useRef, useState } from "react";

import FormCSS from './styles/Form.module.css'

export default function Form() {
    const [stateType, setStateType] = useState('radio') // стейт для смены типа ответа

    const [listTrueAnswer, setListTrueAnswer] = useState([]) // стейт верных ответов
    const [stateTrueId, setStateTrueId] = useState(1) // счетчик айдишников для стейта верных ответов

    const [listFalseAnswer, setListFalseAnswer] = useState([]) // стейт неверных ответов
    const [stateFalseId, setStateFalseId] = useState(1) // счетчик айдишников для стейта неверных ответов
    /* 
        {
        id,
        name,
        text
    }*/ // структура записей в стейтах ответа

    function setAnswers() { // ответы
        if (stateType === 'radio') { // radio btn
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name="answerTrue_1" className={FormCSS.answer} /><br />

                    <p>Неверный: </p>
                    <input type="text" name="answerFalse_1" className={FormCSS.answer} /><br />

                    {/* {listFalseAnswer.map(elem => {
                        return <>
                            <input key={elem.id} type='text' name={elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id)} className={FormCSS.answer} />
                            <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id)}>удалить</button>
                            <br />
                        </>
                    })} */}
                    {mapList('answerFalse')}
                </div>

                <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns('answerFalse')}>Добавить неверный</button>
            </>)


        } else if (stateType === 'text') { // text btn
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name="answerTrue_1" className={FormCSS.answer} /><br />

                    <p>Вариант написания правильного ответа: </p>

                    {listTrueAnswer.map(elem => {
                        return <>
                            <input key={elem.id} type='text' name={elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id)} className={FormCSS.answer} />
                            <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id)}>удалить</button>
                            <br />
                        </>
                    })}
                </div>

                <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns('answerTrue')}>Добавить вариант правильного ответа</button>
            </>)


        } else if (stateType === 'checkbox') {
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name="answerTrue_1" className={FormCSS.answer} /><br />
                    <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns('answerTrue')}>Добавить</button>

                    <p>Неверный ответ: </p>
                    <input type="text" name="answerFalse_1" className={FormCSS.answer} /><br />

                    {listFalseAnswer.map(elem => {
                        return <>
                            <input key={elem.id} type='text' name={elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id)} className={FormCSS.answer} />
                            <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id)}>удалить</button>
                            <br />
                        </>
                    })}
                </div>

                <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns('answerFalse')}>Добавить</button>
            </>)
        }
    }

    function btnAddAns(bool) { // кнопка добавления ответа
        if (bool === 'answerFalse') {
            setStateTrueId(stateTrueId + 1)
            let obj = {
                id: stateTrueId,
                name: bool + '_' + stateTrueId,
                text: ''
            }
            setListTrueAnswer([...listTrueAnswer, obj])
            console.log(listTrueAnswer)
        } else {
            setStateFalseId(stateFalseId + 1)
            let obj = {
                id: stateFalseId,
                name: bool + '_' + stateFalseId,
                text: ''
            }
            setListFalseAnswer([...listFalseAnswer, obj])
            console.log(listFalseAnswer)
        }
    }// /кнопка добавления ответа

    function setTextAnsw(e, id, bool) { // реактивная замена ответа в стейте
        let listAnswer = (bool === 'answerTrue' ? listTrueAnswer : listFalseAnswer)
        let setListAnswer = (bool === 'answerTrue' ? setListTrueAnswer : setListFalseAnswer)

        let copy = [...listAnswer]
        let index = copy.findIndex(elem => elem.id === id)

        copy[index].text = e.target.value
        setListAnswer(copy)
    } // /реактивная замена ответа в стейте

    function btnRemoveAns(id, bool) { // кнопка удаления ответа из стейта
        let listAnswer = (bool === 'answerTrue' ? listTrueAnswer : listFalseAnswer)
        let setListAnswer = (bool === 'answerTrue' ? setListTrueAnswer : setListFalseAnswer)

        let copy = [...listAnswer]
        let index = copy.findIndex(elem => elem.id === id)

        copy.splice(index, 1)
        setListAnswer(copy)
    }// /кнопка удаления ответа из стейта

    function mapList(bool) { // сокращенка для вывода списка ответа
        console.log(bool, '(bool maplist)')
        let res = (bool === 'answerTrue' ? listTrueAnswer : listFalseAnswer).map(elem => {
            // console.log(elem)
            return <>
                <input key={elem.id} type='text' name={elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id, bool)} className={FormCSS.answer} />
                <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id, bool)}>удалить</button>
                <br />
            </>
        })
        console.log(res, '(res mapList)')
        return res
        /* 
        {listFalseAnswer.map(elem => {
            return <>
                <input key={elem.id} type='text' name={elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id)} className={FormCSS.answer} />
