import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom'

import PageCardTest from "./PageCardTest";
import PersCSS from '../styles/Personal.module.css'

export default function MainPagePers() {
    let [data, setData] = useState([]) // [{nameTest: 'Test', id: 'yl4hZ7', length: 1}]
    let params = useParams()

    async function getDataFromDB() {
        let url = window.location.pathname

        if (url == `/personal/${params.name}`) {
            await fetch(`/personal/${params.name}`)
                .then(r => r.json())
                .then(r => setData(r.data))
                .catch(err => console.log(err))
        }
    }


    useEffect(() => {
        getDataFromDB()
    }, [])
    return <>
        <div className={PersCSS.head}>
            <button className={`${PersCSS.switchBtn} ${PersCSS.active}`}>Публичные</button>
            <button className={PersCSS.switchBtn}>Приватные</button>
        </div>

        <div>
            {data ?
                data.map((elem, index) => {
                    return <PageCardTest key={index} name={elem.nameTest} length={elem.length} idTest={elem.id} />
                })
                :
                'load'
            }
        </div>
    </>
}
