import React, { useEffect, useState } from "react";

import ResolCSS from '../styles/Resolution.module.css'
import { useParams } from "react-router-dom";

export default function ResolutionCheck() {
    let [data, setData] = useState()
    let param = useParams()

    async function getData() {
        let data = await fetch(`/resolution/${param.id}/check`).then(r => r.json()).then(r => r)
        setData(data)
        console.log(data)
    }

    useEffect(() => {
        getData()
    }, [])

    function render() {
        let arr = []

        let userAnswer = data.query,
            tureAnswer = data.tureAnswer;

        for (let keyUserAnswer in userAnswer) {
            let res

            res = <><p className={ResolCSS.resP}>{arr.length + 1}. {keyUserAnswer}</p>
                <div className={ResolCSS.variant}>Ответ пользователя: {typeof userAnswer[keyUserAnswer] === 'object' ?
                    userAnswer[keyUserAnswer].join(', ') : userAnswer[keyUserAnswer]
                }</div></>



            arr.push(res)
        }

        return arr

        function span() {

        }
    }

    return <>
        {data ? <>

            <div><h3 className={ResolCSS.resH3}>{data.name}</h3></div>

            {render().map((elem, index) => {
                return <div key={index} className={ResolCSS.res}>
                    {elem}
                </div>
            })}

            <div className={ResolCSS.res}>
                <p className={ResolCSS.resP}>Вопрос</p>
                <div className={ResolCSS.variant}>Ответ пользователя: ответ0 <span className={ResolCSS.comments + ' ' + ResolCSS.false}>// неверно</span>
                    <div className={ResolCSS.comments}>Верный ответ: <span className={ResolCSS.true}>ответ1</span>, ответ2, ответ3</div>
                </div>
            </div>




            <div className={ResolCSS.res}>
                <p className={ResolCSS.resP}>Вопрос</p>
                <div className={ResolCSS.variant}>ответ пользователя <span className={ResolCSS.comments + ' ' + ResolCSS.true}>// верно</span></div>
            </div>

        </>
            :
            <div>load</div>
        }
    </>
}
