import React, { useState } from "react";

import FormCSS from '../styles/Form.module.css'

export default function QUEForm({ elemFromState, number, remove, last, add, mainList, mainSetList }) {
    /* 
        key={index}
        id={elem}
        number={index}
        remove={remove}
        last={index === list.length - 1 ? true : false}
        add={add}
    */




    let num = number + '_'

    // const [stateType, setStateType] = useState('radio') // стейт для смены типа ответа

    // const [listTrueAnswer, setListTrueAnswer] = useState([]) // стейт верных ответов
    // const [stateTrueId, setStateTrueId] = useState(2) // счетчик айдишников для стейта верных ответов

    // const [listFalseAnswer, setListFalseAnswer] = useState([]) // стейт неверных ответов
    // const [stateFalseId, setStateFalseId] = useState(2) // счетчик айдишников для стейта неверных ответов
    /* 
        {
        id,
        name,
        text
    }*/ // структура записей в стейтах ответа

    function setAnswers() { // ответы
        if (stateType === 'radio') { // radio btn
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name={num + "answerTrue"} className={FormCSS.answer} /><br />

                    <p>Неверный: </p>
                    <input type="text" name={num + "answerFalse"} className={FormCSS.answer} /><br />

                    {mapList('answerFalse')}
                </div>

                {lastBtn('answerFalse')}
            </>)


        } else if (stateType === 'text') { // text btn
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name={num + "answerTrue"} className={FormCSS.answer} /><br />

                    <p>Вариант написания правильного ответа: </p>

                    {mapList('answerTrue')}
                </div>

                {lastBtn('answerTrue')}
            </>)


        } else if (stateType === 'checkbox') { // checkbox
            return (<>
                <div className={FormCSS.answer_block}>
                    <p>Верный ответ: </p>
                    <input type="text" name={num + "answerTrue"} className={FormCSS.answer} /><br />

                    {mapList('answerTrue')}

                    <button type="button" className={FormCSS.btnAdd + ' ' + FormCSS.margin_bottom} onClick={() => btnAddAns('answerTrue')}>Добавить</button>

                    <p>Неверный ответ: </p>
                    <input type="text" name={num + "answerFalse"} className={FormCSS.answer} /><br />

                    {mapList('answerFalse')}
                </div>

                {lastBtn('answerFalse')}
            </>)
        }
    }

    function lastBtn(bool) { // сокращенка для стиля последней кнопки 'завершить', "добавить вопрос"
        if (last) {
            return <div className={FormCSS.lastBlock}>
                <button type="button" className={FormCSS.btnAdd + ' ' + FormCSS.lastBtn} onClick={() => btnAddAns(bool)}>Добавить</button>
                <div>
                    <button type="button" className={FormCSS.btnAdd + ' ' + FormCSS.lastBtn} onClick={() => add()}>Добавить вопрос</button>
                    <button type={number === 0 ? 'reset' : 'button'} className={FormCSS.removeAnsBtn} onClick={number !== 0 ? () => remove(elemFromState.id) : undefined}>удалить вопрос</button>
                    <button type="submit" className={FormCSS.btnAdd + ' ' + FormCSS.lastBtn}>Завершить</button>
                </div>
            </div>
        } else {
            return <div className={FormCSS.lastBlock}>
                <button type="button" className={FormCSS.btnAdd} onClick={() => btnAddAns(bool)}>Добавить</button>
            </div>
        }
    }// /сокращенка для стиля последней кнопки

    function btnAddAns(bool) { // кнопка добавления ответа
        let copy = [...mainList]

        let indexMainStait = copy.findIndex(elem => elem.id === elemFromState.id)

        if (bool === 'answerTrue') {
            elemFromState.answerTrue = [...elemFromState.answerTrue, '']
        } else {
            elemFromState.answerFalse = [...elemFromState.answerFalse, '']
        }

        copy[indexMainStait] = elemFromState
        mainSetList(copy)
    }// /кнопка добавления ответа

    function setTextAnsw(e, bool) { // реактивная замена ответа в стейте
        let copy = [...mainList]
        let indexMainStait = copy.findIndex(elem => elem.id === elemFromState.id)

        copy[indexMainStait][bool] = e.target.value
        mainSetList(copy)
    } // /реактивная замена ответа в стейте

    function btnRemoveAns(text, bool) { // кнопка удаления ответа из стейта
        let copy = [...mainList]
        let indexMainStait = copy.findIndex(elem => elem.id === elemFromState.id)
        let indexAnswerFromMainStait = copy[indexMainStait][bool].findIndex(elem => elem === text)

        copy[indexMainStait][bool][indexAnswerFromMainStait] = text

        mainSetList(copy)
    }// /кнопка удаления ответа из стейта

    function mapList(bool) { // сокращенка для вывода списка ответа
        let res = (bool === 'answerTrue' ? listTrueAnswer : listFalseAnswer).map((elem, index) => {
            return <div key={index}>
                <input type='text' name={num + elem.name} value={elem.text} onChange={(e) => setTextAnsw(e, elem.id, bool)} className={FormCSS.answer} />
                <button type="button" className={FormCSS.removeAnsBtn} onClick={() => btnRemoveAns(elem.id, bool)}>удалить</button>
                <br />
            </div>
        })
        return res
    }// /сокращенка для вывода списка ответа

    function setStateTypeReset(text) { // радио кнопка для выбора типа ответа 
        setStateType(text)
        setListTrueAnswer([])
        setListFalseAnswer([])
        setStateTrueId(2)
        setStateFalseId(2)
    }// /радио кнопка для выбора типа ответа

    return (<div className={FormCSS.blockQUE}>
        <textarea className={FormCSS.textarea} name={num + "textQUE"} placeholder="Введите текст вопроса"></textarea>

        <div className={FormCSS.type}>
            <p className={FormCSS.type_para}>Выберите тип ответа:</p>
            <label><input type="radio" name={num + "type"} value='radio' onClick={() => setStateTypeReset('radio')} defaultChecked />Один вариант</label>
            <label><input type="radio" name={num + "type"} value='text' onClick={() => setStateTypeReset('text')} />Текст</label>
            <label><input type="radio" name={num + "type"} value='checkbox' onClick={() => setStateTypeReset('checkbox')} />Выбрать несколько</label>
        </div>
        {setAnswers()}
    </div>)
}
