import React, { useState, useRef } from "react";
import { nanoid } from 'nanoid';

import FormCSS from '../styles/Form.module.css'
import QUEForm from "./QUEForm";

export default function Form() {
    const refNullInput = useRef(null)

    function stopSend() {
        let x = refNullInput.current
        if (x.value.length === 0) {
            alert('Вы заполнили не все поля')
            x.classList.toggle(FormCSS.backRed)
            return true
        }
        return false
    }




    const [list, setList] = useState([
        {
            id: nanoid(5),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
    ])
    /* const [list, setList] = useState({
        id: qwe,
        type: 'type',
        text: 'qwe',
        answerTrue: [],
        answerFalse: [],
    }) */

    function add() {
        let copy = [...list]

        copy = [...copy,
        {
            id: nanoid(5),
            type: 'radio',
            text: '',
            answerTrue: [
                [nanoid(3), '']
            ],
            answerFalse: [
                [nanoid(3), '']
            ],
        }
        ]
        setList(copy)
    }
    function remove(id) {
        let copy = [...list]
        let index = copy.findIndex(elem => elem.id === id)

        copy.splice(index, 1)
        setList(copy)
    }

    return (<>
        <form method="post" onKeyDown={(e) => { if (e.key === 'Enter') e.preventDefault() }}>
            <input className={FormCSS.none} readOnly type="text" name="id" value={nanoid(6)} /> {/* display: none. Ставит айдишник данным для БД */}


            <input type="text" name="nameTest" className={FormCSS.inp_name} placeholder="Введите название теста" ref={refNullInput} autoComplete="off" />

            {list.map((elem, index) => {
                return <QUEForm
                    key={index}
                    elemFromState={elem}
                    number={index}
                    remove={remove}
                    last={index === list.length - 1}
                    add={add}

                    mainList={list}
                    mainSetList={setList}
                    refNullInput={refNullInput}
                    stopSend={stopSend}
                />
            })}
        </form>
    </>)
}
